CREATE TABLE category (id INTEGER PRIMARY KEY AUTOINCREMENT, title TEXT NOT NULL, gallery_id INTEGER NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE gallery (id INTEGER PRIMARY KEY AUTOINCREMENT, title TEXT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE image (id INTEGER PRIMARY KEY AUTOINCREMENT, title TEXT NOT NULL, filepath TEXT NOT NULL, category_id INTEGER NOT NULL, comment TEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
